import React, { Component } from "react";
import "./App.css";

export default class App extends Component {
  state = { data: [], palindrome: "" };

  inputHandeler = (e) => {
    this.setState({ data: e.target.value.split(",") });
    console.log(this.state.data);
  };
  resethandeler = () => {
    this.setState({ data: "", palindrome: "" });
  };

  bubblesort = () => {
    for (let i = 0; i < this.state.data.length; i++) {
      for (let k = 1; k > 0; k--) {
        if (this.state.data[k] < this.state.data[i]) {
          let temp = this.state.data[i];
          this.state.data[i] = this.state.data[k];
          this.state.data[k] = temp;
        } else break;
      }
    }
  };

  render() {
    return (
      <div className="container">
        <h1> Array sorting App</h1>
        <div className="app">
          <div className="inputfield">
            <label htmlFor="sort" name=" sort ">
              Input number ex:2,3,4,5,6 "use coma" and sort the Array:
            </label>
            <input
              id="sort "
              type="text"
              onChange={this.inputHandeler}
              value={this.state.data}
            />
            <div className="button_wrapper">
              <button onClick={() => this.bubblesort(this.state.data)}>
                {" "}
                Sort
              </button>
              <button onClick={this.resethandeler}> Reset </button>
            </div>
          </div>
          <div className="output">{this.state.palindrome}</div>
        </div>
      </div>
    );
  }
}
