import React, { Component } from "react";
import "./App.css";

export default class App extends Component {
  state = { data: [""], palindrome: "" };

  resethandeler = () => {
    this.setState({ data: "", palindrome: "" });
  };

  submitHandeler = (e) => {
    e.preventDefault();
    this.setState({ data: e.target.value });
    console.log(this.state.data);
  };

  render() {
    return (
      <div className="container">
        <h1> Array sorting App</h1>
        <div className="app">
          <div className="inputfield">
            <form onSubmit={this.submitHandeler}>
              <label htmlFor="sort" name=" sort ">
                Input number ex:2,3,4,5,6 "use coma" and sort the Array:
              </label>
              <input id="sort " type="text" value={this.state.data} />
              <div className="button_wrapper">
                <button type="submit"> Sort</button>
                <button onClick={this.resethandeler}> Reset </button>
              </div>
            </form>
          </div>
          <div className="output">{this.state.palindrome}</div>
        </div>
      </div>
    );
  }
}
